	HideUIPanel(DBMOptionsFrame);
	this:SetText(DBMGUI_SHOW_OPTIONS);
else
	ShowUIPanel(DBMOptionsFrame);
	this:SetText(DBMGUI_HIDE_OPTIONS);
end

