Scripts.OnMouseUp:
DBM.Options.Gui.HidePlayerNamesInRaid = not this:GetChecked();

if (DBM.Options.Gui.HidePlayerNamesInRaid 
   and tonumber(GetCVar("UnitNamePlayer")) == 1 
   and GetNumRaidMembers() > 0) then

	DBM.Options.Gui["HidePlayerNames"] = true;
	SetCVar("UnitNamePlayer", 0);

elseif (not DBM.Options.Gui.HidePlayerNamesInRaid
   and DBM.Options.Gui["HidePlayerNames"]) then

	DBM.Options.Gui["HidePlayerNames"] = false;
	SetCVar("UnitNamePlayer", 1);
end

